import React from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';
import { Lock, Star } from 'lucide-react';
import { CalendarDay as CalendarDayType } from '../types';

const DayContainer = styled(motion.div)<{ $isUnlocked: boolean; $isCompleted: boolean; $isToday: boolean; $day: number }>`
  aspect-ratio: 1;
  border-radius: ${props => {
    // Vary border radius for visual interest
    const variations = ['16px', '20px', '24px', '18px'];
    return variations[props.$day % 4];
  }};
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  cursor: ${props => props.$isUnlocked ? 'pointer' : 'not-allowed'};
  position: relative;
  overflow: hidden;
  border: 1px solid;
  transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);

  ${props => {
    if (props.$isCompleted) {
      return `
        background: linear-gradient(135deg, ${props.theme.colors.secondary}, ${props.theme.colors.secondary + '80'}, ${props.theme.colors.accent + '60'});
        border-color: ${props.theme.colors.secondary};
        color: white;
        box-shadow: 0 8px 32px ${props.theme.colors.secondary + '40'};
      `;
    } else if (props.$isUnlocked) {
      return `
        background: linear-gradient(135deg, ${props.theme.colors.primary}, ${props.theme.colors.primary + '80'}, ${props.theme.colors.accent + '40'});
        border-color: ${props.theme.colors.primary};
        color: white;
        box-shadow: 0 6px 24px ${props.theme.colors.primary + '30'};
      `;
    } else {
      return `
        background: linear-gradient(135deg, ${props.theme.colors.background + '60'}, ${props.theme.colors.background + '40'});
        border-color: ${props.theme.colors.text + '20'};
        color: ${props.theme.colors.text + '50'};
        backdrop-filter: blur(10px);
      `;
    }
  }}

  ${props => props.$isToday && `
    animation: pulse 2s ease-in-out infinite;
    box-shadow: 0 0 30px ${props.theme.colors.accent + '80'}, 0 8px 32px ${props.theme.colors.accent + '40'};
    border-color: ${props.theme.colors.accent};

    @keyframes pulse {
      0%, 100% { transform: scale(1); }
      50% { transform: scale(1.05); }
    }
  `}

  &:hover {
    ${props => props.$isUnlocked && `
      transform: translateY(-4px) scale(1.02);
      box-shadow: 0 12px 40px rgba(0, 0, 0, 0.4);
      border-width: 4px;
    `}
  }

  &::before {
    content: '';
    position: absolute;
    top: -2px;
    left: -2px;
    right: -2px;
    bottom: -2px;
    background: ${props => {
      if (props.$isCompleted) return `linear-gradient(45deg, ${props.theme.colors.secondary}, ${props.theme.colors.accent})`;
      if (props.$isUnlocked) return `linear-gradient(45deg, ${props.theme.colors.primary}, ${props.theme.colors.accent})`;
      return 'transparent';
    }};
    border-radius: inherit;
    z-index: -1;
    opacity: 0;
    transition: opacity 0.3s ease;
  }

  &:hover::before {
    ${props => (props.$isUnlocked || props.$isCompleted) && `opacity: 0.3;`}
  }
`;

const DayNumber = styled.div<{ $day: number; $isCompleted: boolean }>`
  font-size: ${props => {
    // Vary font sizes for visual interest
    const sizes = ['22px', '26px', '24px', '28px'];
    return sizes[props.$day % 4];
  }};
  font-weight: 800;
  margin-bottom: 6px;
  text-shadow: 0 2px 8px rgba(0, 0, 0, 0.7);
  position: relative;
  z-index: 10;
  color: white;

  ${props => props.$isCompleted && `
    background: rgba(0, 0, 0, 0.4);
    backdrop-filter: blur(4px);
    border-radius: 12px;
    padding: 4px 8px;
    border: 1px solid rgba(255, 255, 255, 0.2);
  `}
`;

const DayIcon = styled.div<{ $day: number; $isCompleted: boolean }>`
  position: relative;
  z-index: 10;

  ${props => props.$isCompleted && `
    background: rgba(0, 0, 0, 0.4);
    backdrop-filter: blur(4px);
    border-radius: 50%;
    padding: 6px;
    border: 1px solid rgba(255, 255, 255, 0.2);
  `}

  svg {
    width: ${props => {
      // Vary icon sizes
      const sizes = ['18px', '22px', '20px', '24px'];
      return sizes[props.$day % 4];
    }};
    height: ${props => {
      const sizes = ['18px', '22px', '20px', '24px'];
      return sizes[props.$day % 4];
    }};
    filter: drop-shadow(0 2px 8px rgba(0, 0, 0, 0.7));
    color: white;
  }

  span {
    font-size: ${props => {
      const sizes = ['20px', '24px', '22px', '26px'];
      return sizes[props.$day % 4];
    }};
    filter: drop-shadow(0 2px 8px rgba(0, 0, 0, 0.7));
  }
`;

const BackgroundImage = styled.div<{ $image: string; $isCompleted: boolean }>`
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background-image: url(${props => props.$image});
  background-size: cover;
  background-position: center;
  opacity: ${props => props.$isCompleted ? 1 : 0.3};
  z-index: ${props => props.$isCompleted ? 1 : -1};
  border-radius: inherit;

  ${props => props.$isCompleted && `
    &::after {
      content: '';
      position: absolute;
      top: 0;
      left: 0;
      right: 0;
      bottom: 0;
      background: linear-gradient(
        135deg,
        rgba(0, 0, 0, 0.3) 0%,
        rgba(0, 0, 0, 0.1) 50%,
        rgba(0, 0, 0, 0.4) 100%
      );
      border-radius: inherit;
    }
  `}
`;

const CompletedOverlay = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: linear-gradient(
    135deg,
    rgba(22, 163, 74, 0.15) 0%,
    transparent 50%,
    rgba(22, 163, 74, 0.25) 100%
  );
  border-radius: inherit;
  z-index: 2;
`;

const TodayIndicator = styled(motion.div)`
  position: absolute;
  top: -2px;
  right: -2px;
  background: ${props => props.theme.colors.accent};
  color: white;
  width: 24px;
  height: 24px;
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 12px;
  font-weight: 700;
  z-index: 15;

  svg {
    width: 12px;
    height: 12px;
  }
`;

const CompletedBadge = styled(motion.div)`
  position: absolute;
  top: 6px;
  left: 6px;
  background: linear-gradient(135deg, #16a34a, #22c55e);
  color: white;
  width: 28px;
  height: 28px;
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 14px;
  font-weight: 700;
  z-index: 15;
  box-shadow: 0 2px 8px rgba(0, 0, 0, 0.3);
  border: 2px solid rgba(255, 255, 255, 0.3);
  backdrop-filter: blur(4px);

  svg {
    width: 16px;
    height: 16px;
  }
`;

const PointsIndicator = styled.div<{ $isCompleted: boolean }>`
  position: absolute;
  bottom: 6px;
  right: 6px;
  background: ${props => props.$isCompleted ? 'rgba(0, 0, 0, 0.7)' : 'rgba(0, 0, 0, 0.6)'};
  color: white;
  padding: 4px 8px;
  border-radius: 12px;
  font-size: 11px;
  font-weight: 700;
  z-index: 10;
  backdrop-filter: blur(4px);
  border: 1px solid rgba(255, 255, 255, 0.2);
  text-shadow: 0 1px 2px rgba(0, 0, 0, 0.8);

  ${props => props.$isCompleted && `
    box-shadow: 0 2px 8px rgba(0, 0, 0, 0.3);
  `}
`;

interface CalendarDayProps {
  day: CalendarDayType;
  onClick: () => void;
  isToday: boolean;
}

export const CalendarDay: React.FC<CalendarDayProps> = ({ day, onClick, isToday }) => {
  const canOpen = day.isUnlocked && !day.isCompleted;

  const handleClick = () => {
    if (canOpen) {
      onClick();
    }
  };

  const getIcon = () => {
    if (day.isCompleted) {
      return <CheckCircleIcon />;
    } else if (!day.isUnlocked) {
      return <LockClosedIcon />;
    } else {
      return day.theme?.icon ? <span>{day.theme.icon}</span> : null;
    }
  };

  return (
    <DayContainer
      $isUnlocked={day.isUnlocked}
      $isCompleted={day.isCompleted}
      $isToday={isToday}
      $day={day.day}
      onClick={handleClick}
      initial={{ opacity: 0, scale: 0.8 }}
      animate={{ opacity: 1, scale: 1 }}
      transition={{ 
        duration: 0.5, 
        delay: day.day * 0.03,
        type: "spring",
        stiffness: 120,
        damping: 12
      }}
      whileHover={canOpen ? { 
        scale: 1.02,
        y: -2,
        transition: { duration: 0.2, type: "spring", stiffness: 300 }
      } : {}}
      whileTap={canOpen ? { scale: 0.98 } : {}}
    >
      {day.theme?.image && (
        <BackgroundImage $image={day.theme.image} $isCompleted={day.isCompleted} />
      )}

      {day.isCompleted && <CompletedOverlay />}

      {day.isCompleted && (
        <CompletedBadge
          initial={{ scale: 0, rotate: -180 }}
          animate={{ scale: 1, rotate: 0 }}
          transition={{ delay: 0.3, type: "spring", stiffness: 300 }}
        >
          <CheckCircleIcon />
        </CompletedBadge>
      )}

      {isToday && (
        <TodayIndicator
          initial={{ scale: 0 }}
          animate={{ scale: 1 }}
          transition={{ delay: 0.5, type: "spring", stiffness: 300 }}
        >
          <StarIcon />
        </TodayIndicator>
      )}

      <DayNumber $day={day.day} $isCompleted={day.isCompleted}>{day.day}</DayNumber>

      <DayIcon $day={day.day} $isCompleted={day.isCompleted}>
        {getIcon()}
      </DayIcon>

      {day.task && day.task.points > 0 && (
        <PointsIndicator $isCompleted={day.isCompleted}>
          {day.task.points}pts
        </PointsIndicator>
      )}
    </DayContainer>
  );
};
